from collections import Counter

def encontrar_duplicados(lista_numeros):
    """
    Encontra números duplicados em uma lista e retorna um dicionário
    com os números e suas contagens.
    """
    
    # 1. Conta a frequência de cada número na lista
    # Ex: [1, 2, 2, 3] vira {1: 1, 2: 2, 3: 1}
    contagem = Counter(lista_numeros)
    
    # 2. Filtra o dicionário para incluir apenas os números
    #    que apareceram mais de uma vez (qtde > 1)
    duplicados = {numero: qtde for numero, qtde in contagem.items() if qtde > 1}
    
    return duplicados

